import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Family() {
  const [members, setMembers] = useState([]);
  const [form, setForm] = useState({
    role: "",
    name: "",
    age: "",
    address: "",
  });
  const navigate = useNavigate();

  const fetchData = async () => {
    try {
      const res = await axios.get("http://localhost:5000/users/family", {
        withCredentials: true,
      });
      setMembers(res.data);
    } catch (err) {
      console.error("Fetch error:", err);
      if (err.response?.status === 401) {
        alert("Session expired. Please login again.");
        navigate("/login");
      } else {
        alert("Failed to fetch family members");
      }
    }
  };

  const addMember = async (e) => {
    e.preventDefault();
    try {
      if (!form.role || !form.name || !form.age || !form.address) {
        throw new Error("All fields are required");
      }

      await axios.post("http://localhost:5000/users/family", form, {
        withCredentials: true,
        headers: {
          "Content-Type": "application/json",
        },
      });

      setForm({ role: "", name: "", age: "", address: "" });
      fetchData(); // Refresh the list
    } catch (err) {
      console.error("Add error:", err);
      alert(err.response?.data?.error || err.message || "Failed to add member");
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Family Members</h2>
      <form onSubmit={addMember} style={{ marginBottom: "20px" }}>
        <input
          placeholder="Role (e.g., Father, Mother)"
          value={form.role}
          onChange={(e) => setForm({ ...form, role: e.target.value })}
          style={{ margin: "5px", padding: "8px" }}
          required
        />
        <input
          placeholder="Name"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          style={{ margin: "5px", padding: "8px" }}
          required
        />
        <input
          placeholder="Age"
          value={form.age}
          onChange={(e) => setForm({ ...form, age: e.target.value })}
          style={{ margin: "5px", padding: "8px" }}
          required
        />
        <input
          placeholder="Address"
          value={form.address}
          onChange={(e) => setForm({ ...form, address: e.target.value })}
          style={{ margin: "5px", padding: "8px" }}
          required
        />
        <button
          type="submit"
          style={{
            margin: "5px",
            padding: "8px 15px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
          }}
        >
          Add Family Member
        </button>
      </form>

      <h3>Your Family Members:</h3>
      {members.length === 0 ? (
        <p>No family members added yet</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {members.map((m, i) => (
            <li
              key={i}
              style={{
                margin: "10px 0",
                padding: "10px",
                border: "1px solid #ddd",
              }}
            >
              <strong>{m.role}:</strong> {m.name}, Age: {m.age}, Address:{" "}
              {m.address}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Family;
