import React, { useState } from "react";
import axios from "axios";

function Weather() {
  const [city, setCity] = useState("Colombo");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");

  const getWeather = async () => {
    try {
      setError("");
      setWeather(null);
      const response = await axios.get(`http://localhost:5000/api/weather/${city}`);
      setWeather(response.data);
    } catch (err) {
      setError(err.response?.data?.error || "Error fetching weather data");
    }
  };

  return (
    <div style={{ padding: 20, fontFamily: "Arial, sans-serif" }}>
      <h2>MERN Weather App</h2>
      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter city name"
        style={{ padding: "8px", fontSize: "16px" }}
      />
      <button onClick={getWeather} style={{ marginLeft: 10, padding: "8px 16px" }}>
        Get Weather
      </button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {weather && (
        <div style={{ marginTop: 20 }}>
          <h3>Weather in {weather.city}</h3>
          <p>Temperature: {weather.temperature}</p>
          <p>Windspeed: {weather.windspeed}</p>
          <p>Weather code: {weather.weathercode}</p>
          <p>
            Coordinates: {weather.latitude}, {weather.longitude}
          </p>
        </div>
      )}
    </div>
  );
}

export default Weather;
