import React, { useState } from "react";
import axios from "axios";

function Weather() {
  const [city, setCity] = useState("Toronto");
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState("");

  const getWeather = async () => {
    try {
      setError("");
      setWeatherData(null);
      const response = await axios.get(`http://localhost:5000/api/weather/${city}`);
      setWeatherData(response.data);
    } catch (err) {
      setError(err.response?.data?.error || "Error fetching weather");
    }
  };

  // Format timestamp to hour AM/PM
  function formatHour(dt) {
    const date = new Date(dt * 1000);
    let hours = date.getHours();
    const ampm = hours >= 12 ? "pm" : "am";
    hours = hours % 12 || 12;
    return `${hours}${ampm}`;
  }

  return (
    <div style={{ fontFamily: "Arial, sans-serif", maxWidth: 600, margin: "auto", padding: 20 }}>
      <h2>World Weather</h2>
      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter city or town name"
        style={{ padding: 10, fontSize: 16, width: "70%" }}
      />
      <button onClick={getWeather} style={{ padding: 10, marginLeft: 10 }}>
        Get Weather
      </button>

      {error && <p style={{ color: "red", marginTop: 20 }}>{error}</p>}

      {weatherData && (
        <div style={{ marginTop: 30, backgroundColor: "#0077be", color: "white", borderRadius: 10, padding: 20 }}>
          <h3 style={{ margin: 0 }}>{weatherData.location}</h3>
          <small>Updated just now</small>

          <div style={{ display: "flex", alignItems: "center", marginTop: 20 }}>
            <img
              src={`http://openweathermap.org/img/wn/${weatherData.current.weather.icon}@2x.png`}
              alt={weatherData.current.weather.description}
              style={{ width: 80, height: 80 }}
            />
            <div style={{ marginLeft: 20 }}>
              <h1 style={{ margin: 0 }}>{Math.round(weatherData.current.temp)}°C</h1>
              <p style={{ margin: 0, fontSize: 18 }}>{weatherData.current.weather.description}</p>
              <p style={{ margin: 0, fontSize: 14 }}>
                Feels like {Math.round(weatherData.current.feels_like)}°C | Humidity: {weatherData.current.humidity}% | Wind: {Math.round(weatherData.current.wind_speed)} m/s
              </p>
            </div>
          </div>

          {weatherData.alerts.length > 0 && (
            <div style={{ backgroundColor: "#f4a261", color: "black", padding: 10, marginTop: 15, borderRadius: 6 }}>
              <strong>Alerts:</strong>
              {weatherData.alerts.map((alert, i) => (
                <p key={i} style={{ margin: 0 }}>
                  {alert.event}: {alert.description}
                </p>
              ))}
            </div>
          )}

          <h4 style={{ marginTop: 30 }}>Hourly Forecast</h4>
          <div style={{ display: "flex", gap: 10, overflowX: "auto" }}>
            {weatherData.hourly.map((hour, idx) => (
              <div
                key={idx}
                style={{
                  backgroundColor: "#023e8a",
                  borderRadius: 10,
                  padding: 10,
                  minWidth: 70,
                  textAlign: "center",
                  color: "white"
                }}
              >
                <div>{formatHour(hour.dt)}</div>
                <img
                  src={`http://openweathermap.org/img/wn/${hour.weather.icon}.png`}
                  alt={hour.weather.description}
                  style={{ width: 40, height: 40 }}
                />
                <div>{Math.round(hour.temp)}°C</div>
                <div>{Math.round(hour.pop * 100)}% rain</div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default Weather;
