import React, { useState } from "react";
import axios from "axios";

function AddFamily() {
  const [form, setForm] = useState({
    role: "",
    name: "",
    age: "",
    address: "",
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:5000/users/family", form, {
      withCredentials: true,
    });
    alert("Family member added!");
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Family</h2>
      <input placeholder="Role" onChange={(e) => setForm({ ...form, role: e.target.value })} />
      <input placeholder="Name" onChange={(e) => setForm({ ...form, name: e.target.value })} />
      <input placeholder="Age" onChange={(e) => setForm({ ...form, age: e.target.value })} />
      <input placeholder="Address" onChange={(e) => setForm({ ...form, address: e.target.value })} />
      <button type="submit">Add</button>
    </form>
  );
}

export default AddFamily;
