import { useEffect, useState } from "react";
import axios from "axios";

function Family() {
  const [members, setMembers] = useState([]);
  const [form, setForm] = useState({ role: "", name: "", age: "", address: "" });

  const fetchData = async () => {
    try {
      const res = await axios.get("http://localhost:5000/users/family", {
        withCredentials: true,
      });
      setMembers(res.data);
    } catch (err) {
      alert("Fetch error: " + err.response?.data);
    }
  };

  const addMember = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users/family", form, {
        withCredentials: true,
      });
      setForm({ role: "", name: "", age: "", address: "" });
      fetchData();
    } catch (err) {
      alert("Add error: " + err.response?.data);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <form onSubmit={addMember}>
        <input placeholder="Role" value={form.role} onChange={(e) => setForm({ ...form, role: e.target.value })} />
        <input placeholder="Name" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} />
        <input placeholder="Age" value={form.age} onChange={(e) => setForm({ ...form, age: e.target.value })} />
        <input placeholder="Address" value={form.address} onChange={(e) => setForm({ ...form, address: e.target.value })} />
        <button type="submit">Add Family Member</button>
      </form>

      <h3>Your Family:</h3>
      <ul>
        {members.map((m, i) => (
          <li key={i}>{m.role}: {m.name}, Age: {m.age}, Address: {m.address}</li>
        ))}
      </ul>
    </div>
  );
}

export default Family;
